    private void kkSliderStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_kkSliderStateChanged
        // Get value from slidder
        int value = kkSlider.getValue();
        outputText.setBackground(new Color(value, 255,255));
    }//GEN-LAST:event_kkSliderStateChanged

